css:
@-moz-document url-prefix("https://www.gtamulti.com/forum/") {
#wrapper {
    background: #ffffff;
}
body {
    color: #000000;
}
#footer {
    background: #ffffff
}
#footer li, #footer p, #footer a {
    color: #000;
}
.prtema {
    color: #000000;
}
.dropmenu li ul, .top_menu, .dropmenu li li:hover, .button, .dropmenu li li:hover>a, .dropmenu li li a:focus, .dropmenu li li a:hover, #top_section, #search_form .button, .quickbuttons li, .quickbuttons li ul, .quickbuttons li ul li:hover, .quickbuttons ul li a:focus, .popup_window, #inner_section {
    background: #ffffff;
}
.display_title {
    color: white;
}
.bbc_standard_quote {
    background-color: #e0e0e0;
}
#header {
    background: #ffffff;
}
h1.forumtitle {
    background: url(https://i.hizliresim.com/yGWk5M.png);
    height: 130px;
    background-repeat: no-repeat;
}
h1.forumtitle img {
    display: none;
}
.duyuru {
    background: #ffffff;
}
::-webkit-scrollbar {
    width: 10px;
    background-color: #ffffff;
}
}
