css:
@-moz-document domain("hyuu.cc") {
/*=================*/
/* General Styling */
/*=================*/

body {
    background: #191919; }

li {
    list-style-type: none; }

.title, .guide {
    line-height: 1rem;
    margin: 1rem 0; }

/*=======================*/
/* Boxes/Rounded Corners */
/*=======================*/

.server-list li {
    padding: 0.5rem; }

.server-list li:nth-child(odd) {
    border-radius: 6px;
    background: #232323;
    margin: 0.5rem 0; }

.plug {
    border-radius: 6px;
    background: #232323;
    padding: 0.5rem; }

/* FAQ Page */

details {
    border-radius: 6px;
    border: 0;
    background: #232323;
    padding: 0.5rem; }

/*==============*/
/* Link Styling */
/*==============*/

a {
    transition: color 0.2s;
    text-decoration: none; }

a:hover {
    color: #8C9ECC }
}

@-moz-document url("https://hyuu.cc/faq/") {
/*=====================*/
/* FAQ General Styling */
/*=====================*/

h1 {
    margin: 1rem 0 0 0; }

p {
    margin: 0 0 1rem 0; }

hr {
    display: none; }

details {
    transition: background 0.2s;
    background: #232323; }

summary {
    border: 0 !important; }
}
