css:
@-moz-document domain(youtube.com) {
    ytd-rich-item-renderer {
        width: calc(100% / 6 - 16px - 0.01px);
    }
    #video-title.ytd-rich-grid-video-renderer {
        font-size: 1.4rem;
    }
    ytd-video-meta-block[rich-meta] #channel-name.ytd-video-meta-block, 
    ytd-video-meta-block[rich-meta] #metadata-line.ytd-video-meta-block {
        font-size: 1.2rem;
    }
    #avatar-link.ytd-rich-grid-video-renderer {
        display: none;
    }
}
