css:
@-moz-document domain("onedrive.live.com") {
  .od-BasePage,
  .ms-Callout,
  .od-ItemTile2-icon,
  #appLauncher,
  #O365fpcontainerid {
      filter: invert(0.92);
      background-color: white;
  }

  .od-Panel{
      box-shadow: none;
      border-left: 1px solid #dadada;
  }

  /* File Type Icon */
  .od-ItemTile2-icon div img,
  /* Personal Vault Setup Modal User Image  */
  .od-Persona-image,
  /* Manage Access Icon */
  .od-FileNameWithIcon-icon,
  /* New Dropdown Icons */
  .ms-Image,
  /* Office 365 Icons in App Launcher */
  .ms-svg-Icon,
  /* Preview File Icon */
  .OneUp-other-fileIcon,
  /* File/Folder in List Mode */
  .od-DetailsRow-cell--FileIcon,
  /* Folders in Tiles View */
  .ms-Tile-content,
  .ms-Tile-nameplate,
  /* File Thumbnail Image */
  .od-ItemTile2-image,
  .od-ImageTile--isDimensioned,
  /* Folder Content Thumbnail */
  .ms-FolderCover-content,
  /* PDF Reader */
  .od-PdfViewer-container,
  /* User Avatar */
  ._14ggU2yZvNol5U91gfmYQA img,
  /* Information Pane Icon */
  .InfoPane-itemDetails-icon img,
  /* Progress Icon */
  .OperationMonitor-itemType,
  /* Share By  Link Social Media */
  .od-SharingByLink-icon img,
  /* Premium Card Office 365 Icons */
  .od-PlanCardTest-officeAppIcon,
  .od-Button.od-PlanCardTest-buttonPrimary,
  .ms-Icon--CheckMark {
      filter: invert(1);
  }

  .ms-Tile-link * {
      color: white;
  }

  /* Removes Drag Files Here Hint */
  img.od-DragAndDropHint-file,
  .od-DragAndDropHint-text.od-DragAndDropHint-text--grid {
      display: none;
  }
}
