css:
@-moz-document domain("youtube.com") {
ytd-watch-flexy[theater]:not([fullscreen]) #player-theater-container {
    max-height: 720px !important; /*=> Choose the height of the player*/
}

ytd-watch-flexy[theater]:not([fullscreen]) #ytd-player {
    width: var(--ytd-watch-flexy-max-player-width) !important;
    display: block !important;
    margin-left: auto !important;
    margin-right: auto !important;
}
}
