css:
@-moz-document domain(www.ipachart.com) {
body {
    background-color: #262626;
    font-family:'Open Sans', sans-serif!important;
    color: #fff;
    border-radius: 25px;
}
a:link {
    text-decoration: none;
}
a {
    color: #fff;
}
span.interactive.IPA {
    border-radius: 15px;
}
td, th {
    border-radius: 5px;
}
.at tr th:nth-child(2n+1), .at tr .interactive:nth-child(4n+4), .at tr .interactive:nth-child(4n+1), .ct th, .rt th, .vowels .interactive {
    background-color: #000;
}
.at tr th:nth-child(2n), .at tr .interactive:nth-child(4n+2), .at tr .interactive:nth-child(4n+3), .ct td, .rt td {
    background-color: #000;
}
.at td, .ct .blank {
    background-color: #000;
}
.at .impossible {
    background-color: #1c1c1c !important;
}
.at tr:nth-child(1) td:nth-child(1) {
    background-color: black !important;
    min-width: 10em;
}
.text {
    background-color: #202020;
    border-color: #fff;
    border-radius: 15px;
}
}
