css:
@-moz-document domain("zhihu.com") {
body {
    background: #493a63 url(https://www.tsinghua.edu.cn/publish/thu2018/images/footer.jpg) no-repeat fixed;
    background-size: cover;
    background-attachment: fixed;
    background-position: center;
}
.Card, .GlobalSideBar-navNumber, #root {
    background: #ffffff80;
}
.ContentItem-actions {
    background: #ffffff00;
}
.QuestionHeader {
    background: #ffffffa0;
}
.QuestionHeader-footer {
    background: #ffffff00;
}
.AppHeader, .CommentsV2-withPagination, .Topbar, .MoreAnswers .List-headerText, .CommentsV2-footer, .CommentsV2, .SearchTabs, .Topic-bar, .QuestionWaiting-types, .QuestionWaiting-typesTopper {
    background: #DCD9E0;
}
.WikiBoxHeader-IntroTail {
    background: #CAC7D0;
}
.WikiBoxHeader-IntroTail:before {
    background-image: -webkit-gradient(linear, left top, right top, from(hsla(0, 0%, 100%, 0)), to(#CAC7D0));
    background-image: linear-gradient(90deg, hsla(0, 0%, 100%, 0), #CAC7D0);
}
.CommentListV2-header-divider, .Select-plainButton {
    background-color: #CAC7D0;
}
.PushNotifications-count {
    border-color: #DCD9E0;
}
.KfeCollection-PcCollegeCard-root {
    background: none;
}
}
