css:
@-moz-document domain("moodle.ntnu.edu.tw"), domain("moodle2.ntnu.edu.tw") {
html {
	background: none !important;
}

body {
	background: #ffdddd !important;
}

#page {
	border-radius: 25px !important;	
}

#page-wrapper{
	border-radius: 25px !important;	
    background: #ff9595 !important;
}

#page-header{
	background: #948763 !important;
	border-radius: 20px !important;
	margin-bottom: 1em !important;
}

h1 {
	margin: 1em;
	font-weight: normal !important;
	color: #ffffff !important;
}

h2 {
	text-align: center !important;
	color: #77ccee !important;
}	

h3.sectionname {
	color: #EEEEEE !important;
	background-color: #654321 !important;
	font-size: 200% !important;
	line-height: 1.5 !important;
	border-radius: 30px !important;
}

li.section.main.clearfix {
	border: none !important;
	border-radius: 25px !important;
	margin-bottom: 1em !important;
}

div.content {
	background: #eeffff !important;
	padding: 3px 5px !important;
	border-color: #00EE00 !important;
	border-radius: 10px !important;
}

.breadcrumb{
	background: transparent !important;
}

.header {
	background: transparent !important;
}

.course-content .main {
	background: #ecffff !important;
}

#page-navbar.clearfix{
	background: #ffffff !important;
	border-bottom-right-radius: 20px !important;
	border-bottom-left-radius: 20px !important;
}

.block{
	border-radius: 20px !important;
}
}
