css:
@-moz-document domain("brick-hill.com") {
body {
    font-family: Montserrat, sans-serif;
    font-weight: 500;
    color: #000099;
}

nav div.primary {
    background-color: transparent;
    background-image: url('http://brk.bricklanderswebsite.ml/images/r34hill.png');
    padding-top: 2.5%;
    overflow: hidden;
    background-repeat: no-repeat;
}

.dropdown-content.logout-dropdown li * {
    color: #000099;
    background-color: #AAE5A5;
}

.dropdown-content.logout-dropdown li :hover {
    background-color: #8ee18d;
}

body {
    background-color: #AAE5A5;
}
nav div.primary {
    background-color: #AAE5A5
}
nav div.secondary {
    background-color: #93C393
}
nav div.info {
    background-color: #93C393
}
nav div.secondary {
    color: #F8F8FF
}

.alert.error {
    background-color: #b9ffb3;
}

.card .footer {
    background-color: #daffd6;
}

body,
html {
    background-color: #AAE5A5 !important;
}

.index-top-bar {
    background-color: #2a4629;
    margin-top: -20px;
    text-align: center;
    color: #FFF;
}

nav div.primary {
    color: #000099;
    font-size: 1.1em;
}

nav div.secondary {
    color: #000099;
    font-size: .9em;
}

.nav-notif,
.notif {
    margin-top: -4px;
    float: right;
    background-color: #000099;
    padding: 2px 4px;
    color: #fff;
    font-size: .6em;
    font-weight: 600;
    border-radius: 5px;
}

nav div.primary ul li a:hover {
    border-color: #93C393;
}

nav div.secondary ul li a:hover {
    border-color: #AAE5A5;
}

.card .content {
    height: inherit;
    overflow: auto;
    padding: 10px;
    background-color: #a2f2a0;
}

.profile-card.crate img {
    width: 100%;
    border: 1px solid #FFFFFF;
    background-color: #4e6a4c;
    border-radius: 5px;
    padding: 5px;
}

.card .top:not(.absolute).red {
    color: #000099;
    background-color: #93C393;
    border-color: #FFFFFF;
}

.card .top:not(.absolute).orange {
    color: #000099;
    background-color: #93C393;
    border-color: #FFFFFF;
}

.crate-types .active,
.crate-types :hover {
    background-color: #93C393;
    border-left: 4px solid #FFFFFF;
}

.pages .page {
    color: #000099;
    padding: 5px;
    padding-right: 5px;
    font-weight: 600;
    cursor: pointer;
}

a {
    text-decoration: none;
    color: #000099;
}

.light-gray-text {
    color: #00939A;
}

.gray-text {
    color: #000099;
}

.set-list .set-stats li div:first-child {

    color: #000099;
}

.box.shaded {
    background-color: transparent;
}

.agray-text {
    color: #000099;
}

.darkest-gray-text {
    color: #000099;
    ;
}

.card .item-creator {
    overflow: hidden;
    color: #000099;
    font-size: .9em;
}

.ablack-text {
    color: #000099;
}

.label.dark {
    color: #000099;
}

.tabs .tab-body {
    border: 1px solid white;
    border-top-color: transparent;
    background-color: transparent;
}

.tabs .tab.active {
    background-color: #acffaa;
    font-weight: 900;
    border-bottom: 1px solid white;
}

.tabs .tab {
    text-align: center;
    color: #000099;
    background-color: #6b9f6a;
    border-color: white;
}

.hover-card.viewed {
    background-color: #acffaa;
}

.forum-links a {
    color: #000099;
    padding: 0 10px;
}

.label {
    font-size: .9em;
    font-weight: 500;
    color: #000099;
}

.dark-gray-text {
    color: #373786;
}

.board-info a,
.stat .title {
    color: #004;
}

.card .top:not(.absolute).blue {
    color: #000099;
}

.card .top:not(.absolute).blue {
    color: #000099;
    background-color: #8ee18d;
    border-color: #FFFFFF;
}

.button.tab-button.transparent,
button.tab-button.transparent {
    color: #000099;
}

.card .top:not(.absolute) {
    overflow: auto;
    padding: 10px;
    font-weight: 700;
    border-bottom: 1.5px solid;
    border-bottom-color: currentcolor;
    color: #000099;
    background-color: #8ee18d;
    border-color: #FFFFFF;
}

.card .top:not(.absolute).green {
    color: #000099;
    background-color: #8ee18d;
    border-color: #FFFFFF;
}

.card .creator {
    overflow: hidden;
    color: #000000;
    font-size: .9em;
}

.card .item-card-details,
.card .item-content {
    background-color: #AAE5A5;
}

.card .item-card-details .price,
.card .item-content .price {
    overflow: hidden;
    padding-top: 4px;
    color: #fff;
}

.card .name {
    overflow: hidden;
    color: #000099;
    font-weight: 600;
    display: inline-block;
}

.forum-tag {
    position: relative;
    background: #7FB17E;
    border-radius: 4px;
    padding: 6px 8px;
    color: #fff;
    float: right;
}

.item-card-container {
    background-color: #7FB17E;
}

.avatar-card.crate img {
    height: 95px;
    background-color: transparent;
}

.read-more-desc {
    background-color: #AAE5A5;
}

.toggle-user-desc:not(.open)::after {

    background-color: #AAE5A5;

    color: grey;
}

.hover-card:hover {
    background-color: #acffaa;
}

footer {
    background-color: transparent;
}

.mem-cols .col {
    border: 1px solid #acffaa;
}

.mem-cols ul li,
.mem-cols ul li b
{
    color: #000099;
}

footer, footer a {
    color: #53B467;
}
}
