css:
@-moz-document domain("youtube.com") {
  #body {
  	width: fit-content; 
	max-width: 85%
}

#main {
    background: hsla(0, 0%, 93.3%, 0.6);
    padding: 12px 14px;
    border-radius: 18px; 
    margin-top: -3px 
}

html[dark=true] #main {
	background: rgba(28, 28, 28, 0.8);
}

.ytd-comment-renderer .ytd-expander {
    unicode-bidi: plaintext
}

.ytd-comment-renderer paper-button.ytd-expander { padding: 10px 10px 5px }

#columns { margin: 0 100px 0 170px !important }
}
