css:
@-moz-document domain("danlec.com") {
body{
    background: #1c1c1c;
    color: #fff;
}
:root{
    scrollbar-color: purple #1c1c1c;
}
a,.blog-signature a{
    color: #02ee02;
    text-decoration: none;
}
.content{
    background: #3b3b3b;
}
pre{
    background: #1c1c1c;
    scrollbar-color: purple #1c1c1c;
}
code{
    background: transparent;
    color: purple;
}
.blog-signature{
    color: #fff;
}
.header .side:before{
    content: "Dark theme by Maciejozaman | ";
    color: purple;
}
}
