css:
@-moz-document url-prefix("https://www.roblox.com/games/") {
.light-theme a.btn-common-play-game-lg, .dark-theme a.btn-common-play-game-lg {
    background: url(http://web.archive.org/web/20100205163911im_/http://www.roblox.com/images/Play.png) no-repeat;
    border: 0px;
    height: 48px;
    width: 250px;
}
.light-theme a.btn-common-play-game-lg:hover, .dark-theme a.btn-common-play-game-lg:hover {
    background-color: transparent;
}
.game-main-content .game-play-buttons .btn-common-play-game-lg {
    min-width: 0px;
    padding: 0px;
}
.light-theme .icon-play-game,.dark-theme .icon-play-game {
    display: none;
}
}
