css:
@-moz-document url("https://www.startpage.com/"), url("http://www.startpage.com/") {
body {
    overflow: hidden;
}
body::-webkit-scrollbar {
    width: 0 !important;
}
body {
    overflow: -moz-scrollbars-none;
}
body {
    -ms-overflow-style: none;
}
.hero-search__learn-more-separator, .hero-search__learn-more {
    visibility: hidden;
}
}
