css:
@-moz-document domain("tiktok.com") {
.main-body{
    background-color: #151515;
    color: #fff;
}

h4, p, .recommend-item{
    color:#fff;
}

.header-container .header-content {
    background-color: #101010;
    
}

.header-container{
    box-shadow: none;
}

.menu-item-link a{
    color:#fff;
}

.logo-container .logo-link img{
    filter: invert(100%);
}

.info-content p{
    color: #fff !important;
}
.count-infos{
    color: #fff;
}

.arrow-right::before, .arrow-right::after{
    background-color: #fff !important;
}

ul::-webkit-scrollbar-thumb {
  background: #fff !important; 
  border-radius: 5px;
}

.download-container.card h2 {
    color: grey;
}

.download-container.card{
    background-color: #151515;
}

.video-card-big{
    background-color: #151515 !important;
    color: #fff;

}

.music-info a{
    color: #fff !important;
}

}
