css:
/* i really want this to be global */

/** Always hide maintenance cover **/
.maintenance-mode-cover {
    display: none !important;
}

body > *:not(.maintenance-mode-cover) {
    filter: none;
}

html:not([dark]), body:not([dir]) {
    overflow: initial !important;
}

/** Cleanup Gmail **/
.tw-bs.mt-modal.mt-modal-iframe,
.tw-bs.mt-modal.mt-modal-iframe + .modal-backdrop {
    display: none !important;
}

/** Cleanup tawk.to **/
.conversation-participant {
    display: none !important;
}

/** Cleanup Bitrix **/
.help-block-counter-wrap,
.mpl-comment-aux,
.mpl-comment-aux + .feed-com-informers-bottom {
    display: none !important;
}

/** Hide magento admin messages **/
.notification-global,
#message-popup-window-mask,
#message-popup-window {
    display: none !important;
}

/** Floating admin action messages **/
.vnecoms-cp #messages {
    position: fixed;
    bottom: 5px;
    left: 15px;
    z-index: 9999;
}
