css:
@-moz-document url-prefix("https://trello.com/") {
.list-wrapper {
	width: 400px
}
.list-card {
	max-width: 400px;
	padding-top: 10px;
	padding-bottom: 15px;
}
.list-card-details {
	padding-bottom: 10px;
}
.list .list-cards .list-card {
	border-color: #080B0C !important;
	border-width: 2px;
	border-style: solid;
}
.list .list-card:hover {
	border-color: #1c272b !important;
	border-width: 2px;
	border-style: solid;
}
.list-header-name {
	font-size: 20px;
}
.list-header {
	padding-top: 15px;
	padding-bottom: 15px;
}
.list-card-labels {
	top: -5px;
}
.list-card-cover {
	display: none;
}
.list-card-members {
	padding-right: 10px;
}
.badges {
	position: relative;
	bottom: -10px;
}
}
