css:
@-moz-document domain("jirasrv.albb.ir") {
.ghx-detail-description,
.ghx-fieldtype-textarea,
.ghx-fieldname-summary,
.mce-content-body,
.user-content-block,
.action-body,
textarea.iic-widget__summary,
.editable-field,
span.ghx-inner {
    font-family: 'IRANSans';
    direction: rtl;
}

