    font-family: IranSans;
    src:
        url("https://cdn.alibaba.ir/dist/63f00b86/fonts/fe61680.eot#iefix") format("embedded-opentype"), 
        url("https://cdn.alibaba.ir/dist/63f00b86/fonts/d11c490.woff2") format("woff2"),
        url("https://cdn.alibaba.ir/dist/63f00b86/fonts/74955f1.woff") format("woff"),
        url("https://cdn.alibaba.ir/dist/63f00b86/fonts/1604933.ttf") format("truetype");
    font-style: normal;
    font-weight: 300;
}
}
