@-moz-document regexp("https?://www.youtube.com?/(?!my_videos|ad_companion|subscribe_embed|account|yt|red).*") {
html:not(#‚Å†),
    .videoAdUiAdInfoPopup {
        background: url(https://upload.wikimedia.org/wikipedia/commons/f/ff/Solid_blue.svg)fixed #000!important;
        background-size: cover!important
    }
  :not([class^="ytp-"]) {
        background-color: transparent!important
    }
  #progress.ytd-thumbnail-overlay-resume-playback-renderer {
    background-color: #f00 !important; 
    height: 100% !important; 
}
  #notification-count.ytd-notification-topbar-button-renderer {
    background-color: #c00 !important;}

ytd-button-renderer.style-primary[is-paper-button] {
    background-color: var(--yt-spec-call-to-action)!important;
}
 }
