css:
@-moz-document domain("courses.wesbos.com") {
body {
  background: #000;
}

.top-bar {
	margin-bottom: 0;
}

.viewer {
  max-width: initial;
  height: calc(100vh - 93px);
}

.video__player {
	align-self: center;
}

.video-details {
  background: #222;
}

.viewer__selector {
   border-left: 10px solid #000;
}

.videos {
  border-left: none;
}

.video {
  border-right: none;
}

.video:hover {
    background: #aed9f5;
}

.video header.module {
    background: #ffc600;
    color: #000;
}

.jumper a:hover, .jumper a:focus {
  background: #1585cf;
  color: #fff;
}

.jumper,
.jumper__button--next,
.video__journey,
.video__title {
  border: none;
}
}
