css:
@-moz-document url-prefix("https://www.livechart.me") {
body {
    background-color: black;
    color: white;
}
.anime-synopsis {
    color: white;
}
.mark-fab, .anime-card, body th, tbody td, .anime-card .related-links, .considering .anime-card, .watching .anime-card, .completed .anime-card, .main-title, .anime-tags, .anime-synopsis, .anime-studios, .anime-date, .anime-metadata {
    background-color: #262626;
}
.mark-fab:hover {
    background: #353535;
}
.callout {
    background-color: #262626;
    color: white;
}
}
