css:
@-moz-document domain("unitygroup.com"), domain("unity.pl") {
.header__logo img:first-child {
    transform: rotate(180deg);
}
.header {
    filter: invert(100%) hue-rotate(180deg);
}
.header__inner:before {
    content: "..";
    color: red;
    position: relative;
    left: 26px;
    top: -5px;
}
}
