css:
@-moz-document domain(theunderminejournal.com)
{
  body {
    background-color: black;
    color: #fff;
    margin: 0;
    overflow-y: scroll;
    font-family: sans-serif;
    font-size: 14px;
    min-width: 62em;
    height: 100%;
  }

  a {
    text-decoration: none;
    color: white;
  }

  #search-page table th, table.auctionlist th, table.category th {
    padding: 0.4em 0.2em 0; 
    border-bottom: 1px solid #fff;
    font-weight: normal;
  }

  #search-page tr:hover td, table.auctionlist tr:hover td, table.category tr:hover td, td.hover {
    background-color: #123aad;
  }

  #title > div > a img { 
    background-image: url("https://i.ibb.co/FWG3mFW/Theunderminejournal.png");
    background-size: contain;
    background-repeat: no-repeat; max-width: 100%; max-height: 4em;
  }

}
