@namespace url(http://www.w3.org/1999/xhtml);

/*made by chippro19*/

@-moz-document domain("www.chip.de"),
domain("business.chip.de")
{
.Header--Elevated .Header__Container::before, .Header__Container {
	background: #064b71;
	display: -webkit-box;
	display: -ms-flexbox;
	display: flex;
}.bg-white {
	background-color: #edece8;
}body {
	color: #20292a;
	background: #f9f9f9;
	font-family: Helvetica Neue,Helvetica,Arial,sans-serif;
	font-size: 1rem;
	line-height: 1.5;
	-webkit-font-smoothing: antialiased;
	-moz-osx-font-smoothing: grayscale;
	margin: 0;
}.c-brand {
	color: #003a5a;
}.right-col {
	margin-top: -1rem;
	padding-left: .625rem;
	padding-right: .625rem;
	width: 33.33333%;
	float: left;
    display: none !important;
    width: 0px !important;
    height: 0px !important;
  }
}
