/* ==UserStyle==
@name           nos.nl Dark theme 2.0
@namespace      USO Archive
@author         Rob 'n Roll
@description    `Dark theme for the Dutch NOS website. Makes it easier to read at night.Donker thema voor de NOS website. Maakt het makkelijker om 's avonds te lezen.`
@version        20210623.19.48
@license        CC-BY-SA-4.0
@preprocessor   uso
==/UserStyle== */
@-moz-document domain("nos.nl") {
/* Basically version 2.5, but not going to break the style by renaming it. */

/*  Variables */
:root {
/* GENERAL */
    --color: #aaa;
    --color-dark: #666;
    --link-color-hover: #E61E14;
    --background-color: #2a2a2a;
    --devider-line: #333;

    --body-background: #2a2a2a;
    --link-hover: #E61E14;

    --more-articles-h2: #aaa;
    --more-articles-p: #6F6F6F;

    --cookie-notice-background: #222;
    --cookie-notice-border: #555;
    --cookie-notice-boxshadow: #333;

    --regio-background: #222;
    --regio-border: #555;
    --regio-boxshadow: #333;

    --regio-p1: var(--more-articles-h2);
    --regio-p2: var(--more-articles-p);
    --regio-color: var(--color-dark);
    --regio-color-selected: var(--color);
    --regio-button-background: #333;
    --regio-button-color: #aaa;
    --regio-button-border: #555;
    --regio-border-bottom: #3a3a3a;


/* HEADER */
    --header-background: #222;
    --header-border-bottom: solid 1px #000;
    --header-link-color: #aaa;
    --header-link-color-hover: var(--link-hover);

    --header-menu-background: --header-background;
    --header-menu-devider-line: #3a3a3a;
    --header-menu-devider-bar: #666;

    --header-dropdown-color: var(--header-link-color);
    --header-dropdown-color-hover: var(--header-link-color-hover);
    --header-dropdown-border: #000;
    --header-dropdown-scrollbar-color: #555 #333;
    --header-dropdown-devider-line: var(--header-menu-devider-line);

    --header-popout-background: var(--header-background);
    --header-popout-border: solid 1px #000;
    --header-popout-color: var(--header-link-color);
    --header-popout-color-hover: var(--header-link-color-hover);

/* FOOTER */
    --footer-background: #333;
    --footer-color: #aaa;
    --footer-color-hover: var(--link-hover);
    --footer-border-color: #555;

    --fp-footer-nieuws-border-color: #333;

    --fp-background: var(--body-background);
    --fp-footer-background: #333;
    --fp-footer-border-color: #555;
    --fp-footer-color: #aaa;
    --fp-footer-color-hover: var(--link-hover);

    --fp-footer-nieuwsbrief-background: #333;
    --fp-footer-nieuwsbrief-border-color: var(--link-hover);
    --fp-footer-nieuwsbrief-arrow: #222;

    --fp-footer-devider-line: var(--devider-line);

    --tips-en-vragen-background: #222;
    --tips-en-vragen-border: #555;
    --socialmedia-background-color: #222;
    

/* FRONTPAGE */
    --fp-color: #aaa;
    --fp-color-hover: var(--link-hover);
    --fp-white: #ddd;
    --fp-gradient: rgba(51,51,51,0);
    --fp-color-dark: #6F6F6F;

    --fp-aside-border: #333;
    --fp-aside-color: #aaa;
    --fp-aside-devider-line: #3a3a3a;
    --box-shadow: #444;

    --korte-videos-button-background: #222;
    --korte-videos-button-border: #555;

    --fp-collecties-color: #000;

/* ARTIKEL / NIEUWSUUR */
    --breadcrumbs-nos-nieuws: #aaa;
    --breadcrumbs: #666;
    --breadcrumbs-hover: var(--link-hover);

/* SPORT */
    --button-background: #333;

/*    --bg-dark: #222;
    --a-dark: #E61E14;

    --bg-app: #1e1e1e;
    --a-app: #ff6e6e;
    --color-app: #fff;*/
}

}

@-moz-document domain("nos.nl") {
/***********/
/* GENERAL */
/***********/

/*body            { background-color: #222; color: #aaa; }*/
/* niet alle links hebben de andere kleur, dus deze kunnen we niet gebruiken. het zal per pagina/sectie moeten verlopen */
/*main#content a  { color: #aaa; }*/
html {
  overflow-y: scroll;
  scrollbar-color: #555 #333;
}

/* More articles, underneath articles */
.moreArticlesContainer_DUvR0hlq h2
 { color: var(--more-artices-h2); }
.moreArticlesContainer_DUvR0hlq p
 { color: var(--more-articles-p); }

/* Cookie notice */
div#ccm_notification_wrapper
 { background-color: var(--cookie-notice-background) !important;
     border: solid 1px var(--cookie-notice-border);
    box-shadow: 0 2px 6px 0 var(--cookie-notice-boxshadow) !important;
    }
div#ccm_notification_wrapper div#ccm_col_content_regular p:nth-child(2)
 { color: var(--color) !important; }

/* Regio */
div#regio-onboarding
 { background-color: var(--regio-background); border: solid 1px var(--regio-border); }
/* closing icon cross */
div#regio-onboarding button.ewFMmH
 { color: #aaa; }
/* Volg het nieuws uit jouw regio */
div#regio-onboarding h3.dpGnlX
 { color: var(--regio-p1); }
/* Omschrijving */
div#regio-onboarding p.jsycVT
 { color: var(--regio-p2); }
/* Kies je omroep / Button */
div#regio-onboarding button.hcPxRH
 { background-color: var(--regio-button-background); color: var(--regio-color); border: solid 1px var(--regio-border); }
/*Arrow*/
div#regio-onboarding div.ghDCFY svg path
 { color: #000; }
div#regio-onboarding button.hcPxRH:Hover
 { transition: box-shadow 250ms ease-in-out 0s, border 250ms ease-in-out 0s; box-shadow: #111 0px 2px 6px 0px; }

/* Regio - Pop out */
div.rHuWk
 { background-color: var(--regio-background); border: solid 1px var(--regio-border); }
div.rHuWk li.iTHnni
 { border-bottom: solid 1px var(--regio-border-bottom); }
/* closing icon cross */
div.FLPxJ button
 { color: #aaa !important; }
/*Selected*/
div.rHuWk li.iTHnni .idwpre
 { color: var(--regio-color); }
div.rHuWk li.iTHnni .hHPkrj:checked + label
 { font-weight: 500; color: var(--regio-color-selected); }
/*Button*/
div.rHuWk button.hcPxRH
 { background-color: var(--regio-button-background); color: var(--regio-color); border: solid 1px var(--regio-border); }
div.rHuWk button.hcPxRH span
 { color: var(--regio-color); }
/*Arrow*/
div.rHuWk button.hcPxRH svg path
 { color: #000; }
div.rHuWk button.hcPxRH:Hover
 { transition: box-shadow 250ms ease-in-out 0s, border 250ms ease-in-out 0s; box-shadow: #111 0px 2px 6px 0px; }

/* Volg het nieuws uit jouw regio */
div#regio-onboarding
 { background-color: var(--regio-background); border: solid 1px var(--regio-border); }
div#regio-onboarding h3
 { color: var(--color-dark); }
div#regio-onboarding svg
 { color: #aaa !important; }

.homepage,
/* Voor pagina's als de collecties kunnen we geen body.class gebruiken. */
    main#content,.page-search,.page-article,.is-embedded,.page-liveblog,.category-index,.sport-index,.sport.voetbal-uitslagen,.page-weather,.page-broadcasts,.page-traffic,.page-livestream
     { background-color: var(--body-background) !important; color: #aaa !important; }

/***********/
/* HEADER  */
/***********/
    /*Header ribbon*/
/*    header                  { background-color: hidden !important; }*/
    /*Header menu's*/
    header ol#menu li a     { background-color: var(--header-background) !important; }
    header ol#menu li a:hover { color: var(--header-link-color-hover); }

    /*frontpage spacer thingy*/
    #__next              { background-color: var(--body-background) !important; }
    #__next header       { background-color: var(--header-background) !important; border-bottom: var(--header-border-bottom); }
    #__next header li.VPDen::before { border-color: var(--header-menu-devider-bar) !important; }
    #__next header a     { color: var(--header-link-color); }
    /*article spacer thingy*/
/*    header.nav-wrapper      { background-color: hidden !important; }*/
    header.nav-wrapper div[data-ssr-name="components/nos/_global/Navigation/Navigation"] header { background-color: var(--header-background) !important; 
        border-bottom: var(--header-border-bottom); /*can only implement this one when the header bug has been fixed*/ }
    header ol.ioeZJq li a.gHlceu svg path { fill: var(--header-link-color) !important; }
    header ol.ioeZJq li a.gHlceu:Hover svg path { fill: var(--header-link-color-hover) !important; }
    header.nav-wrapper div[data-ssr-name="components/nos/_global/Navigation/Navigation"] header .lastListItem_2AnGWxpe::before { border-color: var(--header-menu-devider-bar) !important; }

/* Drop down menu */
    /*frontpage*/
    #__next header nav.RqeED ol.bMkaCt ol.eXKAcG
     { border: solid 1px var(--header-dropdown-border); }
    #__next header nav.RqeED ol#menu li.diRIme::after
     { border:  solid 1px var(--header-menu-devider-line) !important; }
    #__next header nav.RqeED ol#menu li.diRIme a.kKWaZY
     { color: var(--header-dropdown-color); }
    #__next header nav.RqeED ol#menu li.diRIme a.kKWaZY:Hover
     { color: var(--header-dropdown-color-hover); }
    /*article, liveblog, etc*/
    header.nav-wrapper a
     { color: var(--header-dropdown-color); }
    header.nav-wrapper a:Hover
     { color: var(--header-dropdown-color-hover); }
    header.nav-wrapper li.menuItem_2N2vGE1J::after
     { border:  solid 1px var(--header-menu-devider-line) !important; }

/* Pop-out menu */
/*popout menu button*/
    /*frontpage*/
    header li.VPDen button
    /*article, liveblog, etc*/
,   header.nav-wrapper li.listItem_2fZxURrd.lastListItem_2AnGWxpe button
    { background: var(--header-popout-background) !important; }

    /*devider line*/
    header.buInrW li.VPDen ol.engvEc li.diRIme::after
,   header.nav-wrapper li.listItem_2fZxURrd.lastListItem_2AnGWxpe li.menuItem_2N2vGE1J::after /*a.anchor_30X6uvpW*/
     { border: solid 1px var(--header-menu-devider-line) !important; }

/*popout menu*/
    /*frontpage*/
    header.buInrW ol.engvEc
     { border: var(--header-popout-border); background: var(--header-popout-background); }
/*    header.kqGXut ol.engvEc li.diRIme*/
    header.buInrW ol.engvEc li.diRIme a div.cVUgds span
     { color: var(--header-popout-color); }
    header.buInrW ol.engvEc li.diRIme a:Hover div.cVUgds span.fjrxdu
     { color: var(--header-popout-color-hover); }
    header.buInrW ol.engvEc li.diRIme a svg.iYoqBv path
     { fill: var(--header-popout-color); }

    /*article,liveblog,etc*/
    header.nav-wrapper ol.dropDownMenu_u7yFgFKw.dropDownMenuLarge_CTCzib2a
     { border: var(--header-popout-border) !important; background: var(--header-popout-background); }
    /*title color*/
    header.nav-wrapper ol.dropDownMenu_u7yFgFKw.dropDownMenuLarge_CTCzib2a a div.externalItemContent_3Kkd31i6 span.externalItemTitle_2bF80iPd
     { color: var(--header-popout-color); }
    /*arrow color*/
    header.nav-wrapper ol.dropDownMenu_u7yFgFKw.dropDownMenuLarge_CTCzib2a a svg.externalArrow_1vzKMg_c path
     { fill: var(--header-popout-color); }
header.nav-wrapper ol.dropDownMenu_u7yFgFKw.dropDownMenuLarge_CTCzib2a a:Hover div.externalItemContent_3Kkd31i6 span.externalItemTitle_2bF80iPd
     { color: var(--header-popout-color-hover); }

/**********/
/* FOOTER */
/**********/
/* Frontpage --fp-footer-bg*/
    #__next footer.WUZHM
     { border: solid 1px var(--body-background); border-top: solid 1px var(--fp-footer-border-color) !important; background-color: var(--fp-footer-background); }
    #__next footer.WUZHM a.eYMAjh
     { color: var(--fp-footer-color); }
    #__next footer.WUZHM a.eYMAjh:Hover
     { color: var(--fp-footer-color-hover); }
    #__next footer.WUZHM div.gGTmMB
     { border-top: solid 1px var(--fp-footer-border-color); }
    /*Nieuwsbrief*/
    #__next div.gbpzBW
     { box-shadow: none; border: solid 1px var(--fp-aside-border); }
    #__next input.KYuXf
,   #__next input.bCNkLB
     { background-color: var(--fp-footer-nieuwsbrief-background) !important; border: solid 1px var(--footer-border-color) !important; }
    #__next button.Owlso svg
     { color: var(--fp-footer-nieuwsbrief-arrow); }
    /*Tips en Vragen*/
    #__next footer.WUZHM a.kNXjhK span.fjrxdu
     { color: var(--color) !important; }
    #__next footer.WUZHM a.kNXjhK
     { border-bottom: 1px solid var(--footer-devider-line); }
    #__next footer.WUZHM a.kNXjhK span.fmBjQo
     { color: var(--color-dark) !important; }
    #__next footer.WUZHM a.kNXjhK:Hover span.fjrxdu
     { color: var(--color-hover) !important; }
    #__next footer.WUZHM div.clLqwJ
     { background-color: var(--tips-en-vragen-background); border: solid 1px var(--tips-en-vragen-border) !important; }

    /*social media buttons*/
    #__next footer.WUZHM a.JivGY path
     { color: var(--socialmedia-background-color) !important; }

/* Artikel / Liveblog */
    footer#footer 
     { background-color: var(--footer-background); color: var(--footer-color); border-top-color: var(--footer-border-color); }

    footer#footer div.footer__column
     { border-left-color: var(--footer-border-color); }
    footer#footer div.divide-top
     { border-top-color: var(--footer-border-color); }

    footer#footer a
     { color: var(--footer-color); }
    footer#footer a:Hover
     { color: var(--footer-color-hover); }

/*************/
/* FRONTPAGE */
/*************/
