css:
@-moz-document domain(berrytube.tv){

/* For BerryTube.TV - Recommended for use with MalTweaks' tweaked fullscreen mode */

/* inbox position - does not work correctly outside of chat-only mode */
#mailButtonDiv {
	position: inherit;
	right: 17%;
	margin-top: 20px;
}

/* main chat box */
.chatbuffer {
	color: white;
	background-color: #1d1d1d;
}


/* main chat light-mode on mouse-over (for some transparent emotes) */
.chatbuffer:hover {
    background-color: white;
    color: black;
}

/* text input box */
#chatinput input {
	background-color: #1d1d1d;
	color: white;
	padding-left: 1%;
}

/* user list */
#chatlist {
	background-color: #1d1d1d;
	color: white;
}

/* squee message highlight */
.chatbuffer .highlight {
	background: rgb(123, 104, 45);
}
  
/* join/leave message highlight (BerryTweaks plugin required) */
.berrytweaks-ircify-join {
    color: rgb(0, 240, 0)
}
  
.berrytweaks-ircify-part{
    color: rgb(190, 0, 0)
}

/* URLs */
a:link {
  color: rgb(57, 100, 158);
}

a:visited {
  color: rgb(104, 76, 149);
}

/* squee URLs */
.highlight a:link {
    color: #78a5e9;
}

.highlight a:visited {
    color: #c088f9;
}

/* request message */
.chatbuffer .message.request {
	color: #00bfff;
	font-style: italic;
	font-weight: 700;
}

/* main chat top bar */
#chatControls {
    background-color: #4d4d4d;
	color: white !important;
	padding-right: 7px;
}

/* user list top bar */
#connectedCountWrapper {
	color: #fff;
	background-color: #4d4d4d;
}
}
