css:
@-moz-document domain("patreon.com") {

* :not(select):not(:hover)>header:not(:hover),* >header:not(:hover)
    {                                            /*  Auto-hide header  */
    opacity: 0 !important;
    transition: all .5s !important;
    }

#component-playground p, .reactWrapper p, #component-playground ol, .reactWrapper ol, b, strong
    {
    font-size: 16px !important;
    line-height: 1.25;
    }

*
    {                                            /*  BG = black  (all)  */
    background-color: #000 !important;
    }

*
    {                                            /*  Text = white  (all)  */
    color: #FFF !important;
    }

a
    {                                            /*  Text = gold  (all links)  */
    color: #FA0 !important;
    }

}
