css:
@-moz-document domain("nczas.com") {
html,body{
    background: #1c1c1c;
}
:root{
    scrollbar-color: #355594 #1c1c1c
}
/*HEADER*/
.td-header-style-6 .td-header-top-menu-full{
    background: #1c1c1c;
}
.td-header-style-6 .td-header-top-menu-full a{
    color: #fff;
}
/*HEADER*/
/*STRONA-GŁÓWNA*/
.td-container-wrap{
    background: #1c1c1c;
}
.td_module_wrap .entry-title a{
    color: #355594;
}
.td_module_wrap:hover .entry-title a{
    color: #fff;
}
.td-post-author-name a{
    color: #fff;
}
/*STRONA-GŁÓWNA*/
/*ARTYKUŁ*/
.post{
    background: #1c1c1c;
}
.post header .entry-title{
    color: #355594;
}
.td-post-content,.td-post-content h2,.td-post-content h3{
    color: #fff;
}
.td-post-content .twitter-tweet p{
    color: #fff;
}
/*PODOBNE-ARTYKUŁY*/
.td_mod_related_posts h3 > a{
    color: #355594;
}
.td_mod_related_posts:hover h3 > a{
    color: #fff;
}
/*PODOBNE-ARTYKUŁY*/
/*KOMENTARZE*/
#reply-title,.comment-form-cookies-consent label{
    color: #fff;
}
textarea,input[type="text"], input[type="url"], input[type="tel"], input[type="email"]{
    background: #1c1c1c;
    color: #fff;
}
/*KOMENTARZE*/
/*ARTYKUŁ*/
/*KONTAKT*/
.td-page-content{
    color: #fff;
}
/*KONTAKT*/
/*FOOTER*/
.td-sub-footer-copy:before{
    content: "Ciemny motyw by Maciejozaman";
    color: #355594;
}
/*FOOTER*/
}
