css:
@-moz-document url(https://yarnpkg.com/) {

    :root {
        --body-color: #1a2632;
        --accent-color: #243447;
        --depth-color: #151f29;
        --border-color: #304251;
        --link-color: #03A9F4;
        --icon-color: #8899a6;
        --primary-text-color: #ffffff;
        --secondary-text-color: #2088ba;
        --code-blob-color: #537698;
        --code-color: #6d6d72;
    }

    .hero {
        background-color: var(--body-color) !important;
        position: inherit !important;
        border-bottom: solid black;
    }

    .hero:after, .full-searchbox .ais-SearchBox:before, .full-searchbox .ais-SearchBox:after {
        content: none;
    }

    body, a {
        background-color: var(--body-color);
        color: var(--secondary-text-color);
    }

    svg {
        color: var(--primary-text-color);
    }

    .full-searchbox .ais-SearchBox {
        margin-top: 20px;
        background-color: var(--depth-color) !important;
    }

    .e1udg07c10 span,
    .e11px6xy10 a,
    .e1udg07c2,
    .eh5kbna6 header * {
        color: var(--primary-text-color);
    }

    .e1udg07c9,
    .e1udg07c7,
    .eu4mhjx1 img {
        background-color: var(--primary-text-color);
    }
    
}
