css:
@-moz-document domain("hattrick.org") {
.switchClubTabWrapper a:not(.switchClubTabActive) {
    background-color: rgba(100, 100, 100, .9) !important;
    color: rgba(255, 255, 255, .6) !important;
    transition: .1s all;
}

.switchClubTabWrapper a:not(.switchClubTabActive) img {
    opacity: 0.5;
}

.switchClubTabWrapper a:not(.switchClubTabActive):hover img {
    opacity: 1;
}

.switchClubTabWrapper a:not(.switchClubTabActive):hover {
    color: white !important;
    background-color: rgba(166, 166, 166, .9) !important;
}

}
