css:
@-moz-document url-prefix("https://graph.qq.com") {
/*body背景*/
body {
    background: url("https://zero-1258101461.cos.ap-guangzhou.myqcloud.com/images/131301.jpg") no-repeat;
    background-position: center;
    background-size: cover;
}

/*qq点登陆误操作总会提示让我怎么扫码*/
span.qrlogin_img_out {
    display: none !important;
}

#combine_page {
    height: 340px;
    color: #fff;
    background: rgba(255, 255, 255, 0.2);
    box-shadow: 0 0 0 1px rgba(255, 255, 255, .3) inset, 0 .5em 1em rgba(0, 0, 0, 0.6);
    margin: 10% auto 0 auto;
    border-radius: 5px;
    padding: 20px;
}

.lay_top {
    display: none !important;
}
.accredit_info_op li {
    color: #fff;
}
}

@-moz-document url-prefix("https://xui.ptlogin2.qq.com") {
.login {
    border-radius: 5px;
}
html[stylus-iframe="https://graph.qq.com"] .login,
.qlogin_tips {
    background: none;
    color: #fff;
}
html[stylus-iframe="https://graph.qq.com"] #qlogin_tips_1 {
    color:#cfcfff;
}

html[stylus-iframe="https://graph.qq.com"] #qr_tips {
    display: none !important;
}

html[stylus-iframe="https://graph.qq.com"] .qlogin_show {
    
}
html[stylus-iframe="https://graph.qq.com"] .link {
    color:#fff;
}
}

@-moz-document url-prefix("https://graph.qq.com/oauth2.0/login_jump") {
body {
    background: none;
}
}
