css:
@-moz-document domain("segmentfault.com") {
	[data-article-nav] #article-nav-list {
		max-height: 700px;
	}
	.float-ads {
		max-height: 1px !important;
		min-height: 1px !important;
		height: 1px !important;
		overflow: hidden !important;
		margin-top: -24px;
	}
	/* 代码 */
	pre code,
	li code {
		font-family: Monaco, Microsoft JhengHei, Microsoft YaHei, Consolas, "Courier New", monospace !important;
		background-color: transparent;
	}
	li code,
	p code {
		padding-left: 3px;
		padding-right: 3px;
		background-color: #661f400d;
	}
	@media (min-width: 1200px) {
		.container,
		.container-lg,
		.container-md,
		.container-sm,
		.container-xl {
			max-width: 1340px;
			font-family: -apple-system, BlinkMacSystemFont, Segoe UI, Helvetica, Arial, sans-serif, Apple Color Emoji, Segoe UI Emoji, Segoe UI Symbol !important;
		}
	}
}
