css:
@-moz-document url("http://ledoux.io/bitsy/editor.html") {
body {
    background: #333340;
}
canvas {
    background: black;
}
.panel {
    background: #101010;
}

.panel .bar {
	background: #17171a;
	color: #6767b2;
}

#topbar {
    background: #101010;
}

input {
    background: black;
    color: white;
}

textarea {
    background: black;
    color: white;
}

input[type=checkbox] + label {
    background: #333340;
}

input[type=radio] + label {
    background: #333340;
}

.controlBox input[type=checkbox] + label {
    background: #333340;
}

button {
    background: #333340;
}

.filePickerLabel {
    background: #333340;
}

.controlBox {
    background: #333340;
}

.controlSpan {
    background: #333340;
}

.animationThumbnail.unselected {
    background: #101010;
}

.controlBox .controlSpan {
    background: #101010;
}

.controlBox button {
    background: #101010;
}

.advDialogConditionDiv {
    background: #101010;
}

.controlBox input[type="radio"]:not(:checked) + label {
    background: #101010;
}

.controlBox .controlBox {
    background: #101010;
}

}
