css:
@-moz-document domain("writetogether.space") {
* {
	color: #fff;
}
p, div, h1, h2, h3, h4, h5, h6 {
	color: #fff;
}
main {
    background: #444;
}

.card, .card-header {
    background: #222;
}
.notification-container {
	background: #000;
}

.post-index, .bg-white  {
    background: #000;
    background-color: #000 !important;
}
.page-item.active .page-link{
    background: #4582ec;
    color: #fff;
    font-weight: bold;
    border-color: #fff;
}
.page-item.disabled .page-link{
    color: #fff;
    background: #444;

}
.page-link {
	color: #fff;
    background: #222;
}

.footer {
	background: #222;
}

.spark-settings-tabs .nav-link{
    color: #fff;
}
.spark-settings-tabs .nav-link.active{
    color: #fff;
    background: #333;
}

/* editor box */
textarea.editor-box {
	background: #000;
    color: #fff;
    padding: 10px !important;    
    border: 2px solid #000;
}
.editor-box:focus {
    background: #000;
    color: #fff;
    padding: 10px;    
	border: 2px solid #ccc!important;
}


}
