css:
@-moz-document domain("artstation.com") {
/* homepage */
hello-world,
community-subscriptions,
learning-carousel.card-content-carousel,
products-list.card-content-carousel,
community-prints,
featured-jobs,
posts-list,
featured-magazine-articles,
mobile-apps,
chrome-store-button,
community-books,
community-newsletter,
.gallery-container .gallery-projects .project:nth-child(18)::after,
marketplace-popular-product {
    display: none !important;
}

/* auto hide comments */

#comments {
    display:none !important;
}

.project-comments-title:hover + #comments,
#comments:hover {
    display:block !important;
}

}
