css:
@-moz-document url-prefix("https://loult.family/")
{
  #chat
  {
    background-image: url("https://cdn.pixabay.com/photo/2017/12/10/20/53/santa-clause-3010837_1280.jpg");
    background-size: contain;
    image-rendering: optimizeQuality;
    background-color: rgb(0, 0, 0);
    border-radius: 1em!important;
    display: flex;
    flex-direction: column;
    margin: 0.25em;
  }

  #chat::-webkit-scrollbar
  {
    display: none;
  }
}
