css:
@-moz-document url-prefix("https://toonily.com/") {
  .profile-manga,
  .c-sidebar {
      background-image: none !important;
  }

  body.text-ui-light {
      color: #aaa;
  }

  .wrap .body-wrap,
  body.text-ui-light .site-header .c-header__top {
      background-color: #333 !important;
  }

  h4,
  .widget-title,
  .h5 a,
  h5 a {
      color: #888 !important;
  }

  .wp-manga-chapter a,
  .row.genres a,
  span.chapter a,
  a.btn-link {
      color: #666 !important;
  }

  .wp-manga-chapter a:hover,
  .row.genres a:hover,
  span.chapter a:hover,
  a.btn-link:hover {
      color: #fff !important;
  }

  .tab-summary,
  .wp-manga-chapter {
      color: #666;
  }

  #navigation-ajax, .widget.c-popular .widget-view-more,
  .c-btn_style-1,
  .less-chap {
      background-color: #f9f9f9 !important;
      color: #000 !important;
  }

  #navigation-ajax:hover, .widget.c-popular .widget-view-more:hover,
  .c-btn_style-1:hover,
  .less-chap:hover {
      background: #004fc6 !important;
      color: #f9f9f9 !important;
  }
}
