css:
/*
    
    Theme Name: Amazon Prime Video Recolor

    Creator: Adam-J-Frost.

    Updated: 2019/12/28

    Created: 2019/12/28
    
    Description: Change the colors of Amazon Prime Video and add more if you like!

*/
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.primevideo.com") {
/*[[color]]*/
/*[[bb]]*/
/* <- Profile Picture Start -> */
.pv-nav-account-icon {
    background-image: var(--profile-picture) !important;
    background-size: 40px 40px !important;
    background-repeat: no-repeat !important;
    width: 40px !important;
    height: 40px !important;
    border-radius: 10% !important;
    opacity: 1 !important;
}
.pv-nav-account-icon:hover {
    opacity: 0.75 !important;
}
/* <- Profile Picture End -> */
/* <- Home Page Start -> */
._2p8AHI,
div._1gQKv6 > div > div > div > div > a {
    color: var(--a-color) !important;
}

.z-GEP- ._1XN8b1 ._1n8-io {
    fill: var(--play-btn-color) !important;
    stroke: var(--play-btn-color) !important;
}
.z-GEP-:hover ._1XN8b1 ._1n8-io {
    opacity: 0.70 !important;
}

#pv-nav-logo > div {
    background-image: var(--prime-logo-img) !important;
    background-size: 160px 50px !important;
    background-repeat: no-repeat !important;
    width: 210px !important;
    height: 50px !important;
    box-sizing: border-box;
    padding-left: 80px;
}

._3qC00w + ._2Sg79D {
    filter: invert(100) !important;
}
._2Sg79D img {
    filter: var(--filter-color) !important;
    -webkit-filter: var(--filter-color) !important;
}
.av-grid-beard-prime-logo {
    filter: var(--filter-color) !important;
    -webkit-filter: var(--filter-color) !important;
}

._2XdOju .cgXbwn > div {
    background: var(--progressbar-color) !important;
}
.pv-footer .pv-brand-logo {
    background-image: var(--prime-logo-img) !important;
    background-size: 160px 50px !important;
    background-repeat: no-repeat !important;
    width: 160px !important;
    height: 50px !important;
    box-sizing: border-box;
}
._33ixDQ,
._33ixDQ:link {
    color: var(--a-color);
    -webkit-transition: color .2s ease-in-out;
    -o-transition: color .2s ease-in-out;
    transition: color .2s ease-in-out;
    text-decoration: none;
}
body a._2Oihhg,
body a:active,
body a:hover,
body a:link._2Oihhg,
body a:link:active,
body a:link:hover,
body a:visited._2Oihhg,
body a:visited:active,
body a:visited:hover {
    color: var(--a-hover-color);
}
/*[[mcolor]]*/

/* <- Home Page End -> */
/* <- Show Details Page Start -> */
.o0uko2.o0uko2.o0uko2._356bMd, .o0uko2.o0uko2.o0uko2._356bMd:link, .o0uko2.o0uko2.o0uko2._356bMd:visited {
	color: var(--text-color) !important;
	background-color: var(--play-btn-color) !important;
}
.o0uko2.o0uko2.o0uko2._356bMd:hover, .o0uko2.o0uko2.o0uko2._356bMd:link:hover, .o0uko2.o0uko2.o0uko2._356bMd:visited:hover {
	opacity: 0.75 !important;
}
.avu-content a.av-hover, .avu-content a, .avu-content a, .avu-content a:link.av-hover, .avu-content a:link, .avu-content a:link, .avu-content a.av-hover, .avu-content a, .avu-content a {
	color: var(--common-color) !important;
}
.avu-content a.av-hover, .avu-content a:active, .avu-content a:hover, .avu-content a:link.av-hover, .avu-content a:link:active, .avu-content a:link:hover, .avu-content a:visited.av-hover, .avu-content a:visited:active, .avu-content a:visited:hover {
	color: var(--common-hover-color) !important;
}
._1EXED7 ._1Tunb1 {
    color: var(--common-color) !important;
}
/* <- Show Details Page End -> */
/* <- Player Start -> */
/* <- Top Right Player Controlls Start -> */
.subtitlesAndAudioButton > img {
    background-image: var(--subtitles-and-audio-icon) !important;
    background-size: 32px 32px !important;
    background-repeat: no-repeat !important;
    width: 99999px !important;
    height: 99999px !important;
}
.subtitlesAndAudioButton > img:nth-child(1) {
    overflow: hidden !important;
}

.optionsButton > img {
    background-image: var(--options-icon) !important;
    background-size: 32px 32px !important;
    background-repeat: no-repeat !important;
    width: 99999px !important;
    height: 99999px !important;
}

.volumeButton > img {
    background-image: var(--volume-control-icon) !important;
    background-size: 32px 32px !important;
    background-repeat: no-repeat !important;
    width: 99999px !important;
    height: 99999px !important;
}
div.fullscreenButtonWrapper > div > img {
    background-image: var(--fullscreen-icon) !important;
    background-size: 28px 28px !important;
    background-repeat: no-repeat !important;
    width: 99999px !important;
    height: 99999px !important;
}
.cascadesContainer > div > .webPlayer .divider {
    margin-bottom: -1px;
    width: 1px;
    height: 25px;
    background-color: var(--default-border-color);
    opacity: .25;
}
div.closeButtonWrapper:nth-child(2) > div:nth-child(1) > img:nth-child(1),
.closeXrayButton > img:nth-child(1) {
    background-image: var(--close-button-icon) !important;
    background-size: 28px 28px !important;
    background-repeat: no-repeat !important;
    width: 99999px !important;
    height: 99999px !important;
}

/* <- Top Right Player Controlls End -> */
/* <- XRay Quick View Start -> */
.xrayImageHoverOutline:hover,
.xrayDetailView .detailContainer .detailImageContainer .imageTextContainer.galleryAvailable:hover::after,
.gridTileElement.clickable:hover::after,
.xrayFullView .xrayTab .tabViewModelContainer .xrayDetailView .detailContainer .detailImageContainer .imageTextContainer.galleryAvailable:hover::after,
.xrayQuickView .xrayQuickViewList .xrayQuickViewListItem:hover::after {
    border-color: var(--default-border-color);
}

.playIcon > .svgBackground {
    background-image: var(--play-button-icon) !important;
    background-size: 87px 100px !important;
    background-repeat: no-repeat !important;
    width: 99999px !important;
    height: 99999px !important;
}
.pausedIcon > .svgBackground {
    background-image: var(--pause-button-icon) !important;
    background-size: 87px 100px !important;
    background-repeat: no-repeat !important;
    width: 99999px !important;
    height: 99999px !important;
}
.cascadesContainer > div > .webPlayer .pausedOverlay > .buttons > .animatedPausedIcon {
    background-image: var(--pause-button-icon) !important;
    background-size: 87px 100px !important;
    background-repeat: no-repeat !important;
    width: 99999px !important;
    height: 99999px !important;
    opacity: 0;
    -webkit-animation: pauseIconAnimation 0 ease-out;
    animation: pauseIconAnimation 0 ease-out;
}
div.progressBarContainer > span {
    background: var(--progressbar-color) !important;
}
.cascadesContainer > div > .webPlayer .progressBarContainer > .progressBarHandleWrapper > .progressBarHandle {
    background: var(--progressbar-color) !important;
}

/* <- Seek Buttons Start -> */
.fastSeekBack > div,
.fastSeekForward > div {
    color: var(--common-color) !important;
}
.fastSeekBack > img {
    background-image: var(--fast-seek-icon) !important;
    background-size: 80px 80px !important;
    background-repeat: no-repeat !important;
    width: 99999px !important;
    height: 99999px !important;
}
.fastSeekForward > img {
    background-image: var(--fast-seek-icon) !important;
    background-size: 80px 80px !important;
    background-repeat: no-repeat !important;
    width: 99999px !important;
    height: 99999px !important;
    -webkit-transform: scaleX(-1);
    transform: scaleX(-1);
}


/* <- Seek Buttons End -> */
/* <- Time Start -> */
.time {
    color: var(--common-color) !important;
}

/* <- HD Icon -> */
.hdIcon > img {
    background-image: var(--hd-icon) !important;
    background-size: 32px 21px !important;
    background-repeat: no-repeat !important;
    width: 99999px !important;
    height: 99999px !important;
}
.hd720p,
.hd1080p {
    color: var(--common-color) !important;
}

/* <- Seek Tooltip -> */
.trickplayTime {
    background: var(--common-color);
}
.cascadesContainer > div > .webPlayer .arrowWrapper > .arrow.down {
    border-top: solid var(--default-border-color);
    border-top-width: 7px;
    margin-top: -1px;
}

/* <- Player End -> */

/* <- Watermark Start -> */
#pv-footer > ul > li:nth-child(4):after {
    content: ' | Prime Video Recolor Plus by Adam J Frost' !important;
}
/* <- Watermark End -> */
}
