css:
@-moz-document domain("news.ycombinator.com") {
td,
.title,
.pagetop {
    font-family: sans-serif;
	font-size: medium;
}

.itemlist > tbody > tr > td.title {
    font-size: smaller;
}

.comment,
.yclinks {
    font-family: sans-serif;
    font-size: small;
}

.comhead,
.subtext {
    font-family: sans-serif;
	font-size: x-small;
}

}
