css:
@-moz-document domain("craftegg.jp"),
domain("app-bang-dream-gbp.com") {
    body {
        user-select: auto;
        background-color: rgb(21, 32, 43);
    }

    h1,
    p,
    a:link,
    a:visited {
        color: #fff;
    }

    .detail .img {
        width: auto;
    }

    .info-box {
        background: none;
    }
}
