tainer .activity-mini li, #work-availability-modal .work-availability-edit .accordion-section, .site-footer .footer-main-content, .site-footer .footer-main-content
  {
    border-bottom: 1px solid var(--main-divider);
  }
  
hr{
    background: var(--main-divider) !important;
}
  /* ------------------------------------ end divider ------------------------------------ */
  
  
  /* ------------------------------------ input ------------------------------------ */
  
  form.gen-form fieldset input[type=text], form.gen-form fieldset input[type=search], form.gen-form fieldset input[type=password], form.gen-form fieldset input[type=email], form.gen-form fieldset input[type=url], form.gen-form fieldset input[type=tel], form.gen-form fieldset textarea, form.gen-form fieldset .ql-container .ql-editor, .ql-container form.gen-form fieldset .ql-editor, #upload-flow .dd-input, #upload-flow .selectize-input, input.text-input{ 
    color: var(--white-text);
    background: #242424;
  }
  
  form.gen-form fieldset input[type=text]:focus, form.gen-form fieldset input[type=search]:focus, form.gen-form fieldset input[type=password]:focus, form.gen-form fieldset input[type=email]:focus, form.gen-form fieldset input[type=url]:focus, form.gen-form fieldset input[type=tel]:focus, form.gen-form fieldset textarea:focus, form.gen-form fieldset .ql-container .ql-editor:focus, .ql-container form.gen-form fieldset .ql-editor:focus,#upload-flow .dd-input:focus, #upload-flow .selectize-input:focus, #upload-flow .selectize-input.focus, #upload-flow .dd-input:hover, #upload-flow .selectize-input:hover, input.text-input:focus{	/* typing */
