  }
  div.psst div a{
    color: currentcolor;
    text-decoration: underline;
  }
  div.psst div a:hover{
    color: currentcolor;
    text-decoration: none;
  }
  
  .carbon-text{
    color: var(--primary-text) !important;
  }

  .carbon-poweredby{
    color: var(--tertiary-text) !important;
  }  

  div.notice{
    background: var(--hyperlink-text);
  }
  div.error{
