}

@-moz-document domain("dribbble.com") {
.signed-out-masthead .text-content h1{
      color: var(--black-text);
  }
  
  .signed-out-masthead .text-content p{
      color: var(--tertiary-text)
  }
.auth-sidebar-content svg path{
    fill: black;
}
  
  /* login dialog */
  #signup-overlay .lightbox{
    background-color: var(--primary-bg);
  }
  .already-a-member{
    color: var(--white-text);
  }
  
  /*  login dialog */
  
  .auth-nav p{
    color: var(--white-text);
  }
  
  .auth-content h2{
    color: var(--white-text);
  }
  
  .auth-content p{
    color: var(--primary-text);
  }
  
  .auth-google, a.auth-google, .form-sub.auth-google, .auth-google.btn-talent-application, .form-btn.auth-google{
    background-color: var(--hyperlink-text);
  }
  
  .auth-google, a.auth-google:hover, .form-sub.auth-google:hover, .auth-google.btn-talent-application:hover, .form-sub.auth-google:focus, .auth-google.btn-talent-application:focus, .form-btn.auth-google:hover, .form-btn.auth-google:focus{
    background-color: var(--hyperlink-text-hover);
  }
  
  .auth-connections a.auth-twitter, .auth-connections a.auth-facebook{
    color: var(--primary-text);
    background-color: #2f2f2f;
  }
  
  .auth-connections a.auth-twitter:hover, .auth-connections a.auth-facebook:hover{
    color: var(--primary-text);
    background-color: #3f3f3f;
  }
  hr.divider:after{
    background: var(--primary-bg);
  }
  hr.divider{
    color: var(--secondary-text);
    border-top: 1px solid var(--main-divider);
  }
  .auth-form label{
    color: var(--white-text);
  }
  
  #signup-overlay .email-signup-toggle.on{
    color: var(--white-text);
  }
  
  #signup-overlay .email-signup-toggle:hover{
    color: var(--hyperlink-text-hover);	
  }
  
  #signup-fields div.copy .errorExplanation, .errorExplanation{
    margin-top: 20px;
  }
  
  .signup-reason{
    color: var(--main-brand) !important;
  }
  /*  end login dialog */
  
  
  
  /*  reset password */
  .auth-nav .back-link{
    border-color: var(--main-border);
    color: var(--secondary-text)
  }
  
  input.button{
    background: var(--main-brand);
    color: var(--black-text);
  }
  
  input.button:hover, input.button:active, input.button:focus{
    background: var(--main-brand-hover);
    color: var(--black-text);
  }
  /*  end reset password */
  
  
  /*  signup page */
  form .errorExplanation{
    background: var(--primary-bg);
  }
  
  fieldset.fieldWithErrors{
    background: var(--primary-bg);
  }
  
  input[type="checkbox"]:checked+label:before{
    border: 1px solid var(--hyperlink-text-hover);
  }
  /*  end signup page */
  
  /*  creat profile */
  
  .step-view{
    background: var(--primary-bg);
  }
  
  .step-view-header-title, .form-field.theme-underlined label{
    color: var(--white-text);
  }
  
  .step-view-header-subtitle{
    color: var(--primary-text);
  }
  
  .avatar-upload>label{
    color: var(--white-text);
  }
  
  .show-avatars .avatar-select-item.selected{
    box-shadow: 0 0 0 0px #fff, 0 0 0 4px var(--main-brand);
  }
  
  .dribbble-logo{
    color: var(--main-brand);
  }
  
  .remove-avatar-image{
    box-shadow: 0 0 0 3px var(--primary-bg);
  }
  .step-view-nav .navigation-btn{
    background-color: #2f2f2f;
    color: var(--primary-text);
  }
  
  .close-icon path{
    fill: var(--primary-text);
  }
  
  .step-view-nav .navigation-btn:hover{
    background-color: #3f3f3f;
    color: var(--white-text);
  }
  
  
  .close-icon path:hover{
    fill: var(--white-text);
  }
  
  .select-box-choice, .image-select-box-choice{
    background: #2f2f2f;
    color: var(--white-text);
    border-color: transparent;
  }
  
  .image-select-box-choice.selected, .image-select-box-choice:hover{
    background: #2f2f2f;
    border-color: var(--main-brand);
  }
  
  .image-select-box-choice .image-select-box-description{
    color: var(--primary-text);
  }
  
  .form-field.theme-underlined select{
    background-color: #2f2f2f;
    color: var(--white-text);
  }
  
  .form-field.theme-underlined select.selected, .form-field.theme-underlined .error-marker input{
    color: var(--white-text);
  }
  
  .select-box-choice:hover{
    background: #3f3f3f;
    color: var(--white-text);
  }
  
  .select-box-choice.selected{
    box-shadow: 0 0 0 2px var(--main-brand);
    background: #3f3f3f;
  }
  
  
  .animate-content .image-select-box-choice.selected .image-select-box-description, .animate-content .image-select-box-choice:hover .image-select-box-description{
    color: var(--primary-text);
  }
  
  .animate-content .image-select-box-choice .image-select-box-title{
    color: var(--white-text);
  }
  
  .has-checkbox .image-select-box-choice{
    background: #2f2f2f;
    border-color: transparent
  }
  
  .image-select-box-choice.selected{
    box-shadow: inset 0 0 0 2px var(--main-brand);
  }
  
  .image-select-box-choice .image-select-box-checkbox{
    background: #3f3f3f;
    border-color: #757575;
  }
  
  .image-select-box-choice .image-select-box-checkbox.active{
    background: var(--main-brand);
    background-color: var(--main-brand);
    
  }
  /*  end creat profile */
}

@-moz-document domain("dribbble.com") {
/* ------------------------------------ header ------------------------------------ */
  .nav-wrapper .nav-inner, .nav-wrapper{
      background: #222;
      background: transparent;
  }
  
  .nav-wrapper .nav-inner{
      box-shadow: none;
  }
  
  #logo svg path{
      fill: var(--white-text);
  }
  
  .nav-wrapper a, .nav-wrapper a:active, .nav-wrapper a:visited, .nav-wrapper a:link, .nav-wrapper a:hover{
      color: var(--primary-text);
      font-size: 14px;
  }
  
  .main-nav .links-container>li>a:hover, .main-nav .actions-container>li>a:hover{
      color: var(--white-text);
  }
  
  .main-nav .links-container .left-outlined span, .main-nav .actions-container .left-outlined span{
      border-left: 1px solid var(--main-divider);
  }
  
  /* upload button */
  .updated-nav .primary-btn span,.form-sub.link-btn, .link-btn.btn-talent-application, .form-btn.link-btn, .nav-wrapper .primary-btn span{
      background-color: var(--main-brand);
      color: var(--black-text);
