  }
  
  .updated-nav .search-wrapper.has-animation.show input{
      border:1px solid transparent;
  }
  .header .search-text{
      font-style: italic;
  }
  
  .ui-autocomplete.ui-menu li.title, .display-name, .ui-autocomplete.ui-menu li.shot span.title, .ui-autocomplete.ui-menu li.shot span.name, .shot-thumbnail-container .shot-details-container .user-information .display-name{
