}
  .display-name{
        color: var(--primary-text);
  }
  
  .ui-menu .ui-menu-item a.ui-state-hover, .ui-menu .ui-menu-item a.ui-state-focus, .ui-menu .ui-menu-item a.ui-state-active, .ui-autocomplete.ui-menu li a:hover{
