    border-radius: 4px;
  }
  
  .form.gen-form.shot-filters-form fieldset input[type=text], form.gen-form.shot-filters-form fieldset input[type=search], .nav-search-wrapper.has-animation.show input{
      background-color: #363636;
      color: var(--white-text);
  }
  
  .filter-settings .form-btn.outlined:hover{
      color: currentcolor;
