@-moz-document domain("dribbble.com") {
/* ------------------------------------ Responsive Web Design ------------------------------------ */
@media only screen and (max-width: 959px){
    .filter-subnav .filter-categories{
        border-top: 1px solid var(--main-divider);
    }
    
}

/* mobile nav */
.mobile-nav{
    background: var(--primary-bg)
}

.mobile-nav hr{
    background: var(--main-divider);
}

@media only screen and (max-width: 767px) {
    .mobile-nav-user span{
        color: var(--white-text);
    }
    
    .nav-wrapper .nav-inner, .nav-wrapper{
        background: var(--primary-bg);
    }

    .mobile-nav.is-open:after {
        content: "";
        position: absolute;
        bottom: 0;
        left: 0px;
        top: 100%;
        right: 0px;
        background: rgba(0, 0, 0, .6);
        height: 999999px;
        z-index: 1;
    }
    .nav-icon{
        color: var(--primary-text);
    }
    
    .main-nav .tab-messages{
        margin-right: 10px;
    }
    
    .mobile-dropdown-nav{
        border-left: 1px solid var(--main-divider);
    }
    
    .profile-subnav{
        border: none;
    }
}

.scrolling-subnav .scroll.scroll-forward a{
    background-image: linear-gradient(270deg, #1b1b1b 50%, rgba(27,27,27,0.00) 100%);
    color: var(--white-text);
}
.scrolling-subnav .scroll.scroll-backward a{
