css:
@-moz-document url-prefix("https://watchnebula.com/videos/") {
:root {
    --backdark: #111;
    --deepgray: #444;
    --subgray: #555;
    --darkgray: #aaa;
    --textgray: #ccc;
    --lightgray: #eee;
}

body, #NebulaApp .NavBar, .css-1wtgkpz, .css-ljxgln {
    background: var(--backdark);
    color: var(--lightgray);
}

.VideoSquare-Header, #NebulaApp .Channel .Channel-Tabs > .menu > a.active, .css-kjiwuj {
    color: var(--lightgray);
}

.VideoPlayer-iFrameContainer .corner {
    background: transparent;
    position: absolute;
    height: 0px;
    width: 0px;
    z-index: 99;
}

.ui.header, .css-ncyty2, .css-1t4jlpu {
    color: var(--textgray);
}

#NebulaApp .Video-Description, #NebulaApp .NavBar-Left .item, .css-13glol9 {
    color: var(--darkgray);
}


/* search bar */
input.css-1wrd3iw {
    background-color: var(--deepgray);
    caret-color: var(--textgray);
    color: var(--textgray);
}


/* follow button */
.css-fcydeg, .css-cab24t {
    background-color: var(--deepgray);
    border: 0;
    transition: all 0.2s ease;
}
.css-fcydeg:hover, .css-cab24t:hover {
    background-color: var(--subgray);
    border: 0;
}


/* rounded corners */
.VideoPlayer-iFrameContainer {
    border-radius: 1em;
}
.ui.tabular.menu .active.item {
    background: none #444;
    color: #eee;
}
#NebulaApp .Channel .Channel-Tabs > .menu {
    border-bottom: 3px solid #444;
}
}
