css:
/* Nifty at night*/
 
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("www.nifty.org") {
body {background:#181818!important; color:white!important; text-align:justify;}
P{color:white!important;}
a:link {color:#BA98CE!important;}
a:visited {color:#826A90!important;}
PRE{background:#181818!important; color:#CCC!important; text-align:justify; position: absolute; left: 30%;}
.well, .btn, .container-fluid{background:black;}
SPAN[style="color:black"]{color:#CCC!important;}
SPAN[style="color:navy"]{color:#CCC!important;}
SPAN[style="font-size:14.0pt;\a color:navy"]{color:#CCC!important;}
SPAN[style="font-size:14.0pt;color:navy"]{color:#CCC!important;}
SPAN[style="font-size:26.0pt;color:navy"]{color:#CCC!important;}
SPAN[style="font-size:10.0pt;color:navy"]{color:#CCC!important;}
SPAN[style="font-size:8.0pt;color:navy"]{color:#CCC!important;}
SPAN[style="font-size:16.0pt;color:navy"]{color:#CCC!important;}
SPAN[style="font-family:Arial;color:navy"]{color:#CCC!important;}
TD{background:#181818!important;}
.panel-body{background:#181818!important;}
.panel-heading{background:#181818!important;}
.panel-title{color:#CCC!important;}
TABLE{align:center;}
}
