css:
@-moz-document url-prefix("https://openclipart.org") {
*{box-sizing:border-box;}
    html,
    body {
        height: 100%;
    }

    body {
        padding: 0;
        margin: 0;
        font: 16px/1.5 'Gill Sans', 'Gill Sans MT', Calibri, 'Trebuchet MS', sans-serif;
        color: #000;
        height: 100%;
    }

    .gallery {
        display: grid;
        grid-gap: 1em;
        grid-template-columns: repeat(auto-fit, minmax(240px, 1fr));
        max-width: 1100px;
        margin: 0 auto;
    }

    .gallery .artwork {
        text-align: center;
        align-self: center;
        border: 1px solid #bbb;
        height: 100%;
        width: 100%;
        max-height: 240px;
        box-shadow: 0 2px 2px 0 #00000030;
        border-radius: 3px;
        background: repeating-linear-gradient(rgba(255, 255, 255, 0.3) 0px, rgba(255, 255, 255, 0.3) 23px, rgba(105, 155, 197, 0.3) 23px, rgba(105, 155, 197, 0.3) 24px) repeat 0% 0%, repeating-linear-gradient(90deg, rgba(255, 255, 255, 0.3) 0px, rgba(255, 255, 255, 0.3) 23px, rgba(105, 155, 197, 0.3) 23px, rgba(105, 155, 197, 0.3) 24px) repeat 0% 0%;
    }

    .artwork img {
        width: 100%;
        height: 100%;
        object-fit: scale-down;
    }
}
